                           {
                                        v = ( $('osszeg').value / $('arfolyam').value * 280 ) - $('osszeg').value;
                                        orig = v = Math.round( v );
                                        if ( v > 9999 )
                                        {   
                                            k = 0; tmp = ''; v = v + ''; v = v.split( '' );
                                            for( i = v.length - 1; i >= 0; i-- ) { tmp = v[i] + ( ( k++  % 3 ) == 0 ? ' ' : '' ) + tmp; }
                                        } else { tmp = v; }
                                        if ( !isNaN( orig ) ) { $( 'veszteseg' ).innerHTML = tmp + ' Ft&nbsp;'; }
                                        else { $( 'veszteseg' ).innerHTML = 'Hibás adat&nbsp;'; }
                                    }
